import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";
//editar tutorial pero sin poder cambiar el id

export default class Tutorial extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: TutorialDataService.getTutorialId(),
            title: "",
            description: "",
            published: false
        };
    }

    handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;
        this.setState({
            [name]: type === "checkbox" ? checked : value
        });
    };

    editTutorial = async (event) => {
        event.preventDefault();

        const { title, description, published } = this.state;
        const data = { title, description, published };

        try {
            const response = await TutorialDataService.update(id, data);
            console.log("Tutorial creado:", response.data);

            this.setState({
                title: "",
                description: "",
                published: false
            });
        } catch (error) {
            console.error("Error al crear el tutorial:", error);
        }
    };

    render() {
        return (
        <div>
            <h2>Edit Tutorial</h2>
            <form onSubmit={this.editTutorial}>
                    <div className="form-group">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text"
                            className="form-control"
                            id="title"
                            name="title"
                            value={this.state.title}
                            onChange={this.handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="description">Description</label>
                        <input
                            type="text"
                            className="form-control"
                            id="description"
                            name="description"
                            value={this.state.description}
                            onChange={this.handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="checkbox"
                            id="published"
                            name="published"
                            checked={this.state.published}
                            onChange={this.handleInputChange}
                        />
                        <label htmlFor="published">Published</label><br />
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-success" onClick={this.editTutorial}>
                            Submit
                        </button>
                    </div>
                </form>
        </div>
        );
    }
}
