import React, { Component } from "react";

export default class AddTutorial extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: "",
            title: "",
            description: "",
            published: false
        };
    }
    handleInputChange = (event) => {
        const { name, value, type, checked } = event.target;
        this.setState({
            [name]: type === "checkbox" ? checked : value
        });
    };

    addTutorial = (event) => {
        event.preventDefault();
        console.log("Datos del tutorial:", this.state);
    };

    render() {

        return (
        <div>
            <h2>Add Tutorial</h2>
            <form name="form">
            <div className="form-group">
                <label htmlFor="id">Id</label>
                <input
                type="text"
                className="form-control"
                id="id"
                required
                />
            </div>
            <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                type="text"
                className="form-control"
                id="title"
                required
                />
            </div>
            <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                type="text"
                className="form-control"
                id="description"
                required
                />
            </div>
            <div className="form-group">
                <input type="checkbox" id="published" value="published"/>
                <label htmlFor="published">Published</label><br></br>
            </div>
            <div className="form-group">
                <button type="submit" className="btn btn-success" onClick={this.addTutorial}>
                Submit
                </button>
            </div>
            </form>
        </div>
        );
    }
}
